seqM	=  ""
seqH	=  ""
seqR    =  ""
edit_distance_MH	=	0 #set	initial	distance	a s	zero
edit_distance_MR	=	0 #set	initial	distance	a s	zero
edit_distance_HR	=	0 #set	initial	distance	a s	zero
for	i	in	range(len(str(seqM))): #compare	each	amino	acid
						if	str(seqM)[i]!=str(seqH)[i]:		
				                 edit_distance_MH	+=	1 #add	 a	 score	 1	 if	 amino	 acids	 are	different
								
for	i	in	range(len(str(seqM))): #compare	each	amino	acid
						if	str(seqM)[i]!=str(seqR)[i]:		
				                 edit_distance_MR	+=	1 #add	 a	 score	 1	 if	 amino	 acids	 are	different
for	i	in	range(len(str(seqH))): #compare	each	amino	acid
						if	str(seqH)[i]!=str(seqR)[i]:		
				                 edit_distance_HR	+=	1 #add	 a	 score	 1	 if	 amino	 acids	 are	different
percentage_of_amino_acids_MH=(len(str(seqH))-edit_distance_MH)/len(str(seqH))*100
percentage_of_amino_acids_MR=(len(str(seqH))-edit_distance_MR)/len(str(seqH))*100
percentage_of_amino_acids_HR=(len(str(seqH))-edit_distance_HR)/len(str(seqH))*100


print(edit_distance_MH,str(percentage_of_amino_acids_MH)+'%')
print(edit_distance_MR,str(percentage_of_amino_acids_MR)+'%')
print(edit_distance_HR,str(percentage_of_amino_acids_HR)+'%')
